// Convert this program to C++//// change to C++ io//// change to one line comments//// change defines of constants to const//// change array to vector<>//// inline any short function#include <iostream>#include <vector>using namespace std;const int N = 40;template <class summation>inline void sum(summation &accum, int countOfNumbers, vector<summation> collection){    accum = 0;    for (int i = 0; i < countOfNumbers; ++i)        accum += collection[i];}int main(){    vector<int> collection;    for (int i = 0; i < N; ++i) {        collection.push_back(i);    }    int accum;    sum(accum , N ,collection);    cout << "Summation of the collection in first N item is -> " << accum << endl;    return 0;}